mapper.select:
SELECT 

FROM fund_info_open a

WHERE a.id = #{id}

SELECT
FROM fund_info_open a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.fund_code LIKE '%'||#{fundCode}||'%' '%'+#{fundCode}+'%' concat('%',#{fundCode},'%')
AND a.notice_title LIKE '%'||#{noticeTitle}||'%' '%'+#{noticeTitle}+'%' concat('%',#{noticeTitle},'%')
AND a.notice_type = #{noticeType}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM fund_info_open a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE fund_info_open SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
